function clients_owl(){jQuery(".field--name-field-clients-logo").owlCarousel({items:2,margin:10,dots:!0,autoPlay:3e3,navigation:!0,responsive:{500:{items:2,dots:!0,navigation:!0},700:{items:3,dots:!0,navigation:!0},900:{items:4,dots:!0,navigation:!0}}})}function service_owl(){jQuery(".field--name-field-service").owlCarousel({items:1,margin:10,dots:!0,autoPlay:3e3,navigation:!0,responsive:{500:{items:1,dots:!0,navigation:!0},700:{items:2,dots:!0,navigation:!0},900:{items:3,dots:!0,navigation:!0}}})}function theme_menu(){jQuery("#main-menu").smartmenus(),jQuery(".region-primary-menu nav").addClass("col-sm-10"),jQuery(".navbar-toggle").click(function(){jQuery(".region-primary-menu").slideToggle()}),jQuery(window).width()<767&&jQuery(".region-primary-menu li a:not(.has-submenu)").click(function(){jQuery(".region-primary-menu").hide()})}function theme_home(){jQuery(".flexslider").flexslider({animation:"slide"}),jQuery(".region-primary-menu a").click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var e=jQuery(this.hash);if(e=e.length?e:jQuery("[name="+this.hash.slice(1)+"]"),e.length)return jQuery("html,body").animate({scrollTop:e.offset().top},1e3),!1}})}wow=new WOW({boxClass:"wow",animateClass:"animated",offset:0,mobile:!1,live:!0}),jQuery(document).ready(function($){wow.init(),clients_owl(),service_owl(),theme_menu(),theme_home()});